package com.ratmirdudin.controller;import com.google.gson.Gson;import com.google.gson.GsonBuilder;import com.ratmirdudin.models.Book;import org.jetbrains.annotations.NotNull;import java.util.List;import java.util.stream.Collectors;public class Application {    public static void main(@NotNull String[] args) {        if (args.length != 1) {            System.out.println("***************WARNING***************");            System.out.println("Please, start program with argument = Author's name.");            System.out.println("You can use and set up a special task \"startMainClass\" of group \"launch\".");            System.out.println("*************************************");            return;        }        String authorName = args[0];        Library library = LibraryFactory.createLibrary();        List<Book> bookListOfChosenAuthor = library.getBookList()                .stream()                .filter(book -> book.getAuthor().getFullName().equals(authorName))                .collect(Collectors.toList());        Gson gson = new GsonBuilder()                .setPrettyPrinting()                .disableHtmlEscaping()                .create();        System.out.println(gson.toJson(bookListOfChosenAuthor));    }}